import { createReducer, on } from "@ngrx/store";
import { list } from "src/app/list/list";
import { addListSuccess, getListSuccess } from "../Actions/list.action";

export interface User {
  name: string;
}
export interface List {
  list: ReadonlyArray<User>;
}

const initialState: ReadonlyArray<User> = [];

export const listReducer = createReducer(
  initialState,
  on(getListSuccess, (state) => [...list]),
  on(addListSuccess, (state, payload) => [...state, payload])
);
